import React, { Component } from 'react'

class Login extends Component {
    constructor(props) {
        super(props)
        this.state = {
             name:'',
             isDisable: true,
             password:''
        }
    }
    handleUsernameChange = (event) => {
        this.setState({
            name: event.target.value
        })
    }
    handleUserPasswordChange = (event) => {

        this.setState({
            password: event.target.value,
            isDisable: false,  
        })
    }

    render() {
        const { name, password } = this.state;
        const enabled =
          name.length > 0 &&
          password.length > 0;
        return (
            <div>
                <h1>Login</h1>
                <label>name</label>
                <input type="text" placeholder="enter your name"
                 value={this.state.name}  onChange = { this.handleUsernameChange }/> 
                <br/>
                <label>password</label>
                <input type="text" placeholder="enter your password"
                value={this.state.password}  onChange = { this.handleUserPasswordChange }/><br/><br/>
                <button type="button" disabled={!enabled}>submit</button>
            </div>
        )
    }
}

export default Login
